// filepath: [HomePage.tsx](http://_vscodecontentref_/0)
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import SearchBar from '../components/SearchBar';
import DataTable from '../components/DataTable';
import UserDetails from '../components/UserDetails';
import CompanyDetails from '../components/CompanyDetails';
import { fetchData } from '../services/api';

const HomePage: React.FC = () => {
    const [data, setData] = useState<any[]>([]);
    const [filteredData, setFilteredData] = useState<any[]>([]);
    const [searchField, setSearchField] = useState<string>('');
    const [targetValue, setTargetValue] = useState<string>('');

    useEffect(() => {
        const getData = async () => {
            try {
                const result = await fetchData();
                setData(result);
                setFilteredData(result);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
        getData();
    }, []);

    const handleSearch = (field: string, value: string) => {
        setSearchField(field);
        setTargetValue(value);

        if (value) {
            const filtered = data.filter(item =>
                item[field]?.toString().toLowerCase().includes(value.toLowerCase())
            );
            setFilteredData(filtered);
        } else {
            setFilteredData(data);
        }
    };

    return (
        <div className="container">
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <h1>People Directory</h1>
                <Link
                    to="/visualization"
                    style={{
                        padding: '10px 20px',
                        backgroundColor: '#007bff',
                        color: 'white',
                        textDecoration: 'none',
                        borderRadius: '4px',
                        fontSize: '1rem',
                    }}
                >
                    See Chart
                </Link>
            </div>
            <SearchBar onSearch={handleSearch} />
            {filteredData.length === 1 ? (
                searchField === 'first_name' ? (
                    <UserDetails user={filteredData[0]} />
                ) : searchField === 'company_name' ? (
                    <div className="company-details">
                        <CompanyDetails company={filteredData[0]} />
                        <video
                            controls
                            width="600"
                            style={{ marginTop: '20px', borderRadius: '8px' }}
                        >
                            <source src="/videos/videoDO I PUT ON F.mp4" type="video/mp4" />
                            Your browser does not support the video tag.
                        </video>
                    </div>
                ) : (
                    <p>No matching results found.</p>
                )
            ) : (
                <DataTable data={filteredData} searchField={''} targetValue={''} />
            )}
        </div>
    );
};

export default HomePage;
